mapper.insert:
INSERT INTO address_book (

) VALUES (
	  #{idx}
	, #{username}
	, #{name}
	, #{postcode}
	, #{address}
	, #{detailAddress}
	, IFNULL(#{defaultYn}, 'N')
	, 'N'
	, NOW()
	, NULL
)

mapper.update:
UPDATE address_book
SET
	  update_time = NOW()

	, default_yn = #{defaultYn}

WHERE
	username = #{username}
