mapper.insert:
INSERT INTO attach (

) VALUES (
	  #{idx}
	, #{code}
	, #{originalName}
	, #{storedName}
	, IFNULL(#{size}, 0)
	, 'N'
	, NOW()
	, NULL
)

mapper.update:
UPDATE attach
SET
	  update_time = NOW()
	, original_name = #{originalName}
	, stored_name = #{storedName}
	, size = IFNULL(#{size}, 0)
	, options= #{options, typeHandler=com.dy.handler.JsonTypeHandler}
WHERE
	code = #{code}
