mapper.insert:
INSERT INTO authority (

) VALUES (
	  #{idx}
	, #{username}
	, IFNULL(#{name, typeHandler=org.apache.ibatis.type.EnumTypeHandler}, 'MEMBER')
	, 'N'
	, IFNULL(#{username}, 'ADMIN')
	, NOW()
	, NULL
	, NULL
)

mapper.delete:
DELETE FROM authority
WHERE
	username = #{username}
AND
	name = #{name}

mapper.select:
SELECT COUNT(*) FROM authority WHERE delete_yn = 'N'
AND username = #{username}
AND name = #{name, typeHandler=org.apache.ibatis.type.EnumTypeHandler}
