mapper.insert:
INSERT INTO goods (

) VALUES (
	  #{idx}
	, #{code}
	, #{name}
	, #{description}
	, #{price}
	, IFNULL(#{status}, 'Y')
	, 'N'
	, NOW()
	, NULL
)

mapper.update:
UPDATE goods
SET
	  update_time = NOW()
	, name = #{name}
	, description = #{description}
	, price = #{price}
	, status = #{status}
WHERE
	code = #{code}
