mapper.insert:
INSERT INTO review (

) VALUES (
	  #{idx}
	, #{username}
	, #{code}
	, #{nickname}
	, #{content}
	, #{grade}
	, 'N'
	, NOW()
	, NULL
)

mapper.update:
UPDATE review
SET
	  update_time = NOW()

  	, content = #{content}


  	, grade = #{grade}

WHERE
	username = #{username}
AND
	idx = #{idx}
