mapper.insert:
INSERT INTO user (

) VALUES (
	  #{idx}
	, #{username}
	, #{password}
	, #{nickname}
	, #{phone}
	, 1
	, 1
	, 1
	, 1
	, 0
	, IFNULL(#{username}, 'ADMIN')
	, NOW()
	, NULL
	, NULL
	, NULL
)

mapper.select:
SELECT

FROM
	user

	enabled = 1

		AND username = #{username}


		AND nickname = #{nickname}


		AND phone = #{phone}

mapper.update:
UPDATE user
SET
	  update_time = NOW()
	, update_user = IFNULL(#{username}, 'ADMIN')

	, nickname = #{nickname}


	, password = #{password}


	, phone = #{phone}

WHERE
	username = #{username}
