mapper.insert:
INSERT INTO user (

) VALUES (
	  #{idx}
	, #{username}
	, #{password}
	, #{nickname}
	, #{phone}
	, #{accountNonExpired}
	, #{accountNonLocked}
	, #{credentialsNonExpired}
	, #{enabled}
	, 'N'
	, IFNULL(#{username}, 'ADMIN')
	, NOW()
	, NULL
	, NULL
)

mapper.update:
UPDATE user
SET
	  update_time = NOW()
	, update_user = IFNULL(#{username}, 'ADMIN')
	, nickname = #{nickname}
	, phone = #{phone}

	, password = #{password}

WHERE
	username = #{username}
